desktop.comment:
Enter number [Numeric] for display product in one row (Screen width &gt; 1240px)
						<script type="text/javascript">
						var desktop = document.getElementById("vsourz_catproduct_settings_desktop");
						var att = document.createAttribute("onkeypress");
						att.value = "return isNumberKey(event)";
						desktop.setAttributeNode(att);
						function isNumberKey(evt)
						{
						  var charCode = (evt.which) ? evt.which : evt.keyCode;
						  if (charCode != 46 && charCode > 31 
							&& (charCode < 48 || charCode > 57))
							 return false;
						  return true;
						}
						</script>

